import { createStore } from 'vuex'

export default createStore({  
  state: {
    user: null,
    prizes: [
      { id: 1, name: '360话费', count: 5, probability: 5 },
      { id: 2, name: '咖啡券', count: 60, probability: 15 },
      { id: 3, name: '充电宝', count: 30, probability: 10 },
      // 修改前：{ id: 4, name: '卡套插线板', count: 30,, probability: 1 },
      { id: 4, name: '卡套插线板', count: 30, probability: 10 },
      { id: 5, name: '网红小夜灯', count: 30, probability: 10 },
      { id: 6, name: '小风扇', count: 50, probability: 15, image: '' },
      { id: 7, name: '泡面碗', count: 30, probability: 10, image: '' },
      { id: 8, name: '轻奢水杯', count: 30, probability: 10, image: '' },
      { id: 9, name: '炫彩雨伞', count: 50, probability: 15, image: '' }
    ],
    hasDrawn: false,
    winningPrize: null
  },
  mutations: {
    setUser(state, user) { state.user = user },
    setHasDrawn(state, status) { state.hasDrawn = status },
    setWinningPrize(state, prize) { state.winningPrize = prize },
    decreasePrizeCount(state, prizeId) {
      const prize = state.prizes.find(p => p.id === prizeId)
      if (prize && prize.count > 0) prize.count--
    }
  },
  actions: {
    // 登录验证
    login({ commit }, { phone, password }) {
      // 这里使用模拟数据，实际项目需要替换为API调用
      const validPhones = ['13800138000', '13900139000'] // 示例有效手机号
      if (validPhones.includes(phone) && password === phone.slice(-6)) {
        commit('setUser', { phone })
        const hasDrawn = localStorage.getItem(`hasDrawn_${phone}`) === 'true'
        commit('setHasDrawn', hasDrawn)
        return true
      }
      return false
    },
    // 抽奖逻辑
    drawPrize({ state, commit }) {
      if (state.hasDrawn) return null

      // 计算可用奖品和总权重
      const availablePrizes = state.prizes.filter(p => p.count > 0)
      const totalWeight = availablePrizes.reduce((sum, p) => sum + p.probability, 0)

      // 随机抽取奖品
      const random = Math.random() * totalWeight
      let currentWeight = 0
      let winningPrize = null

      for (const prize of availablePrizes) {
        currentWeight += prize.probability
        if (random <= currentWeight) {
          winningPrize = prize
          break
        }
      }

      if (winningPrize) {
        commit('decreasePrizeCount', winningPrize.id)
        commit('setWinningPrize', winningPrize)
        commit('setHasDrawn', true)
        localStorage.setItem(`hasDrawn_${state.user.phone}`, 'true')
        localStorage.setItem(`winningPrize_${state.user.phone}`, JSON.stringify(winningPrize))
      }

      return winningPrize
    }
  }
})
